  this.input.on('pointerdown', (pointer) => {
    // 创建 Graphics 对象用于绘制三角形
    const graphics = this.add.graphics();

    // 设置填充颜色为红色
    graphics.fillStyle(0xff0000, 1);

    // 计算三角形的三个顶点坐标
    // 以点击点为中心，创建一个等边三角形
    const size = 32;
    const height = size * Math.sqrt(3) / 2; // 等边三角形的高

    // 顶点1：顶部中心
    const x1 = pointer.x;
    const y1 = pointer.y - height * 2 / 3;

    // 顶点2：左下角
    const x2 = pointer.x - size / 2;
    const y2 = pointer.y + height / 3;

    // 顶点3：右下角
    const x3 = pointer.x + size / 2;
    const y3 = pointer.y + height / 3;

    // 绘制填充三角形
    graphics.fillTriangle(x1, y1, x2, y2, x3, y3);
  });

  // 添加提示文本
  this.add.text(400, 300, '点击画布任意位置生成红色三角形', {
