  // 设置世界边界
  this.physics.world.setBounds(0, 0, WORLD_WIDTH, WORLD_HEIGHT);

  // 获取主相机
  const camera = this.cameras.main;

  // 设置相机边界，限制相机只能在场景范围内移动
  camera.setBounds(0, 0, WORLD_WIDTH, WORLD_HEIGHT);

  // 绘制背景网格以便观察相机移动
  const graphics = this.add.graphics();

  // 绘制网格
  graphics.lineStyle(1, 0x444444, 0.8);
  for (let x = 0; x <= WORLD_WIDTH; x += 100) {
    graphics.lineBetween(x, 0, x, WORLD_HEIGHT);
  }
  for (let y = 0; y <= WORLD_HEIGHT; y += 100) {
    graphics.lineBetween(0, y, WORLD_WIDTH, y);
  }

  // 绘制场景边界（红色边框）
  graphics.lineStyle(4, 0xff0000, 1);
  graphics.strokeRect(2, 2, WORLD_WIDTH - 4, WORLD_HEIGHT - 4);

  // 在四个角落添加标记
  const cornerSize = 50;
  graphics.fillStyle(0xff0000, 0.8);
  // 左上角
  graphics.fillRect(10, 10, cornerSize, cornerSize);
  // 右上角
  graphics.fillRect(WORLD_WIDTH - cornerSize - 10, 10, cornerSize, cornerSize);
  // 左下角
  graphics.fillRect(10, WORLD_HEIGHT - cornerSize - 10, cornerSize, cornerSize);
  // 右下角
  graphics.fillRect(WORLD_WIDTH - cornerSize - 10, WORLD_HEIGHT - cornerSize - 10, cornerSize, cornerSize);

  // 添加中心标记
  graphics.fillStyle(0x00ff00, 0.8);
  graphics.fillCircle(WORLD_WIDTH / 2, WORLD_HEIGHT / 2, 30);

  // 添加文字说明（固定在相机视口上）
  const instructionText = this.add.text(10, 10, 
    'Use Arrow Keys to move camera\nCamera bounds: 1600x1200\nRed corners mark scene boundaries', 
    {
      fontSize: '16px',
      fill: '#ffffff',
      backgroundColor: '#000000',
      padding: { x: 10, y: 5 }
    }
  );
  instructionText.setScrollFactor(0); // 固定在相机上，不随相机移动

  // 显示相机位置信息
  this.positionText = this.add.text(10, 100, '', {
    fontSize: '14px',
    fill: '#ffff00',
    backgroundColor: '#000000',
    padding: { x: 10, y: 5 }
  });
  this.positionText.setScrollFactor(0);

  // 创建键盘控制
  this.cursors = this.input.keyboard.createCursorKeys();

  // 相机移动速度
  this.cameraSpeed = 5;
}

function update(time, delta) {
  const camera = this.cameras.main;

  // 使用方向键控制相机移动
  if (this.cursors.left.isDown) {
    camera.scrollX -= this.cameraSpeed;
  }
  if (this.cursors.right.isDown) {
    camera.scrollX += this.cameraSpeed;
  }
  if (this.cursors.up.isDown) {
    camera.scrollY -= this.cameraSpeed;
  }
  if (this.cursors.down.isDown) {
    camera.scrollY += this.cameraSpeed;
  }

  // 更新相机位置显示
  this.positionText.setText(
    `Camera Position: (${Math.round(camera.scrollX)}, ${Math.round(camera.scrollY)})\n` +
    `Camera Bounds: 0-${camera.getBounds().width - camera.width}, 0-${camera.getBounds().height - camera.height}`
  );
}

new Phaser.Game(config);
```

[PLAN]
REQ: 每隔1秒在随机位置生成紫色矩形，最多12个
API: Phaser.Time.Clock#addEvent, Phaser.GameObjects.Graphics, Phaser.GameObjects.Rectangle
STEPS:
1. 创建 Scene 并初始化计数器变量
2. 在 create 中使用 Graphics 生成紫色矩形纹理
3. 使用 this.time.addEvent 创建定时器，每1秒触发一次
4. 在回调函数中生成随机坐标并创建矩形
5. 当生成数量达到12个时移除定时器
[/PLAN]

```javascript
class GameScene extends Phaser.Scene {
  constructor() {
    super('GameScene');
    this.rectangleCount = 0;
    this.maxRectangles = 12;
    this.timerEvent = null;
  }

  preload() {
    // 无需加载外部资源
  }

  create() {
    // 使用 Graphics 生成紫色矩形纹理
    const graphics = this.add.graphics();
    graphics.fillStyle(0x9932cc, 1); // 紫色
    graphics.fillRect(0, 0, 40, 40);
    graphics.generateTexture('purpleRect', 40, 40);
    graphics.destroy();

    // 创建定时器事件，每1秒执行一次
    this.timerEvent = this.time.addEvent({
      delay: 1000, // 1秒
      callback: this.spawnRectangle,
      callbackScope: this,
      loop: true
    });

    // 显示提示文本
    this.add.text(10, 10, 'Spawning purple rectangles...', {
      fontSize: '16px',
      color: '#ffffff'
    });

    // 显示计数器文本
    this.counterText = this.add.text(10, 30, `Count: 0 / ${this.maxRectangles}`, {
      fontSize: '16px',
      color: '#ffffff'
    });
  }

  spawnRectangle() {
    // 检查是否已达到最大数量
    if (this.rectangleCount >= this.maxRectangles) {
      // 移除定时器
      if (this.timerEvent) {
        this.timerEvent.remove();
        this.timerEvent = null;
      }

      // 显示完成提示
      this.add.text(10, 50, 'All rectangles spawned!', {
        fontSize: '16px',
        color: '#00ff00'
      });
      return;
    }

    // 生成随机位置（确保矩形完全在画布内）
    const x = Phaser.Math.Between(20, 780);
    const y = Phaser.Math.Between(70, 580);

    // 创建紫色矩形
    const rect = this.add.image(x, y, 'purpleRect');

    // 添加简单的缩放动画效果
    this.tweens.add({
      targets: rect,
      scale: { from: 0, to: 1 },
      duration: 300,
      ease: 'Back.easeOut'
    });

    // 增加计数器
    this.rectangleCount++;

    // 更新计数器文本
    this.counterText.setText(`Count: ${this.rectangleCount} / ${this.maxRectangles}`);
  }

  update(time, delta) {
    // 本示例不需要每帧更新逻辑
  }
}

// Phaser 游戏配置
const config = {
  type: Phaser.AUTO,
  width: 800,
  height: 600,
  backgroundColor: '#2d2d2d',
  scene: GameScene
};

// 创建游戏实例
new Phaser.Game(config);
```

