    // 改变为黄色
    block.setTexture('yellowBlock');
    text.setText('拖拽中...');
  });

  // 监听拖拽事件
  block.on('drag', (pointer, dragX, dragY) => {
    // 更新方块位置
    block.x = dragX;
    block.y = dragY;
  });

  // 监听拖拽结束事件
  block.on('dragend', (pointer) => {
    // 恢复青色
    block.setTexture('cyanBlock');

    // 回到初始位置
    block.x = initialX;
    block.y = initialY;

    text.setText('拖拽方块试试！');
