  // 生成纹理
  graphics.generateTexture('orangeEllipse', 100, 80);
  graphics.destroy(); // 销毁 graphics 对象，因为已经生成纹理

  // 创建物理精灵
  const ellipse = this.physics.add.sprite(400, 300, 'orangeEllipse');

