    scaleX: 0.48,  // 缩放到 48%
    scaleY: 0.48,  // 缩放到 48%
    duration: 2500, // 持续 2.5 秒
    ease: 'Sine.easeInOut', // 使用平滑的缓动函数
    yoyo: true,     // 动画结束后反向播放（恢复原始大小）
    loop: -1        // 无限循环播放
  });

