const config = {
  type: Phaser.AUTO,
  width: 800,
  height: 600,
  backgroundColor: '#2d2d2d',
  scene: { preload, create }
};

function preload() {
  // 无需预加载外部资源
}

function create() {
  // 监听画布的点击事件
  this.input.on('pointerdown', (pointer) => {
    // 在点击位置创建红色三角形
    createTriangle(this, pointer.x, pointer.y);
  });

  // 添加提示文字
  this.add.text(400, 300, '点击画布任意位置生成红色三角形', {
    fontSize: '20px',
    color: '#ffffff',
    align: 'center'
  }).setOrigin(0.5);
