  type: Phaser.AUTO,
  width: 800,
  height: 600,
  backgroundColor: '#2d2d2d',
  scene: {
    preload: preload,
    create: create
  }
};

function preload() {
  // 不需要加载外部资源
}

function create() {
  // 使用 Graphics 绘制三角形
  const graphics = this.add.graphics();

  // 设置填充颜色为蓝色
  graphics.fillStyle(0x4a90e2, 1);

  // 绘制三角形（等边三角形）
  graphics.beginPath();
  graphics.moveTo(0, -40);      // 顶点
  graphics.lineTo(-35, 30);     // 左下角
  graphics.lineTo(35, 30);      // 右下角
  graphics.closePath();
  graphics.fillPath();

  // 添加描边使三角形更明显
  graphics.lineStyle(3, 0xffffff, 1);
  graphics.strokePath();

  // 将 Graphics 转换为纹理
  graphics.generateTexture('triangle', 80, 80);
