  });

  // 监听拖拽开始事件
  triangle.on('dragstart', (pointer, dragX, dragY) => {
    // 拖拽时放大到 1.2 倍
    triangle.setScale(1.2);
    // 提升显示层级，确保在最上层
    triangle.setDepth(1);
  });

  // 监听拖拽过程事件
  triangle.on('drag', (pointer, dragX, dragY) => {
    // 更新三角形位置到鼠标位置
    triangle.x = dragX;
    triangle.y = dragY;
  });

