      // 添加一些动画效果（缩放动画）
      star.setScale(0);
      this.tweens.add({
        targets: star,
        scale: 1,
        duration: 300,
        ease: 'Back.easeOut'
      });

      // 增加计数
      starCount++;

      // 达到最大数量后移除定时器
      if (starCount >= maxStars) {
        timerEvent.remove();
        console.log('已生成12个星形，停止生成');
      }
    },
    callbackScope: this,
    loop: true             // 循环执行
  });

  // 添加提示文本
  const text = this.add.text(10, 10, '星形数量: 0 / 12', {
    fontSize: '20px',
    color: '#ffffff',
    backgroundColor: '#000000',
    padding: { x: 10, y: 5 }
  });

  // 更新文本显示
