function update(time, delta) {
  // 将 delta 从毫秒转换为秒
  const deltaSeconds = delta / 1000;

  // 计算移动距离
  const distance = SPEED * deltaSeconds;

  // 重置速度
  let velocityX = 0;
  let velocityY = 0;

  // 检测方向键并设置速度
  if (cursors.left.isDown) {
    velocityX = -1;
  } else if (cursors.right.isDown) {
    velocityX = 1;
  }

  if (cursors.up.isDown) {
