  const graphics = this.add.graphics();
  graphics.fillStyle(0xffffff, 1);

  // 绘制菱形（四个顶点坐标）
  graphics.beginPath();
  graphics.moveTo(32, 0);    // 上顶点
  graphics.lineTo(64, 32);   // 右顶点
  graphics.lineTo(32, 64);   // 下顶点
  graphics.lineTo(0, 32);    // 左顶点
  graphics.closePath();
  graphics.fillPath();

  // 生成纹理
