  graphics.destroy();

  // 创建菱形精灵，初始位置在左侧
  const diamond = this.add.sprite(100, 300, 'diamond');

  // 创建补间动画：从左到右移动
  this.tweens.add({
    targets: diamond,
    x: 700,              // 目标 x 坐标（右侧）
    duration: 1000,      // 持续时间 1 秒
    ease: 'Linear',      // 线性缓动
    yoyo: true,          // 启用往返效果（到达终点后反向播放）
    loop: -1,            // 无限循环（-1 表示永久循环）
    repeat: 0            // yoyo 模式下不需要额外的 repeat
  });

  // 添加提示文本
  this.add.text(400, 50, '白色菱形往返循环动画', {
    fontSize: '24px',
    color: '#ffffff'
  }).setOrigin(0.5);
}

