  const angle = Math.PI / 4; // 45度角
  this.star.setVelocity(
    Math.cos(angle) * speed,
    Math.sin(angle) * speed
  );

  // 启用世界边界碰撞
  this.star.setCollideWorldBounds(true);

  // 设置反弹系数为1（完全弹性碰撞）
